import React from 'react';
import { Link } from 'react-router-dom';
import CircularProgress from '../CircularProgressBar/CircularProgress';
import Div from '../Div';

export default function PageHeading({ title, bgSrc, pageLinkText, showCircularProgress = false }) {
  return (
    <Div
      className="cs-page_heading cs-style1 cs-center text-center cs-bg"
      style={{ backgroundImage: `url(${bgSrc})` }}
    >
      <Div className="container">
        <Div className="cs-page_heading_in">
          <h1 className="cs-page_title cs-font_50 cs-white_color">{title}</h1>
          <ol className="breadcrumb text-uppercase">
            {showCircularProgress && <CircularProgress  value={50} />}
            <li className="breadcrumb-item">
              <Link to="/">Home</Link>
            </li>
            <li className="breadcrumb-item active">{pageLinkText}</li>
          </ol>
        </Div>
      </Div>
    </Div>
  );
}
